import React, { createContext, useContext } from "react";
import "../Card/Card.css";
import JsonData from "../../data.json";

import MovieIcon from "../../assets/icon-category-movie.svg";
import TvIcon from "../../assets/icon-category-tv.svg";

const Card = () => {
  return (
    <>
      <CardDetails />
    </>
  );
};

// Card Image
const CardDetails = () => {
  const recommendedData = JsonData.slice(5);
  const recommendedImages = recommendedData.map((film) => {
    return (
      <div className="card" key={film.title}>
        <img
          className="card-cover"
          src={film.thumbnail.regular.small}
          alt="film cover"
        />

        <div className="detail-container">
          <p>{film.year}</p>
          <p>
            {film.category === "Movie" ? (
              <img src={MovieIcon} alt="movie logo" />
            ) : (
              <img src={TvIcon} alt="tv logo" />
            )}
            {film.category}
          </p>
          <p>{film.rating}</p>
        </div>
      </div>
    );
  });
  return <div className="card-container">{recommendedImages}</div>;
};

export default Card;
