import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useContext } from "react";
import "../Navbar/Navbar.css";

import Logo from "../../assets/logo.svg";
import Home from "../../assets/icon-nav-home.svg";
import Movies from "../../assets/icon-nav-movies.svg";
import TVSeries from "../../assets/icon-nav-tv-series.svg";
import Bookmark from "../../assets/icon-nav-bookmark.svg";
import User from "../../assets/image-avatar.png";
import { UserContextProvider } from "../State Context/StateContext";

const Navbar = () => {
  const [activeNav, setActiveNav] = useState("nav-logo");

  const handleActiveNav = () => {
    setActiveNav("nav-logo active");
  };
  return (
    <nav className="navbar-container">
      <img className="nav-logo" src={Logo} alt="logo" />
      <ul className="nav-links">
        <Link to="/">
          <li>
            <img
              className={activeNav}
              src={Home}
              alt="home logo"
              onClick={handleActiveNav}
            />
          </li>
        </Link>
        <Link to="/movies">
          <li>
            <img
              className={activeNav}
              src={Movies}
              alt="movies logo"
              onClick={handleActiveNav}
            />
          </li>
        </Link>
        <Link to="/shows">
          <li>
            <img
              className={activeNav}
              src={TVSeries}
              alt="tv series logo"
              onClick={handleActiveNav}
            />
          </li>
        </Link>
        <Link to="/bookmarks">
          <li>
            <img
              className={activeNav}
              src={Bookmark}
              alt="bookmark logo"
              onClick={handleActiveNav}
            />
          </li>
        </Link>
      </ul>
      <img className="user-img" src={User} alt="user" />
    </nav>
  );
};

export default Navbar;
