import React, { useState } from "react";
import CardDetails from "../Card Details/CardDetails";
import JsonData from "../../data.json";
import Navbar from "../Navbar/Navbar";
import Search from "../Search/Search";
import Trending from "../Trending Content/Trending";

const Home = () => {
  const [searchValue, setSearchValue] = useState({
    query: "",
    results: [],
  });
  const recommendedData = {
    data: searchValue
      ? JsonData.slice(5).filter((film) => film.title === searchValue)
      : JsonData.slice(5),
    header: "Recommended for you",
    placeholder: "Search for movies or TV series",
  };

  console.log(JsonData.slice(5).filter((film) => film.title === searchValue));

  const handleSearch = (e) => {
    const results = JsonData.slice(5).filter((film) => {
      if (e.target.value === "") return JsonData;
      return JsonData.slice(5).map((film) => {
        return film.title.toLowerCase().includes(e.target.value.toLowerCase());
      });
    });
    setSearchValue({ query: e.target.value, list: results });
  };

  return (
    <>
      <Navbar />
      <Search
        setSearchValue={setSearchValue}
        searchValue={searchValue}
        handleSearch={handleSearch}
        relevantData={recommendedData}
      />
      <Trending />
      <CardDetails relevantData={recommendedData} />
    </>
  );
};

export default Home;
