import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { useContext } from "react";
import "../Navbar/Navbar.css";

import Logo from "../../assets/logo.svg";
import Home from "../../assets/icon-nav-home.svg";
import Movies from "../../assets/icon-nav-movies.svg";
import TVSeries from "../../assets/icon-nav-tv-series.svg";
import Bookmark from "../../assets/icon-nav-bookmark.svg";
import User from "../../assets/image-avatar.png";

const Navbar = () => {
  const [activeNav, setActiveNav] = useState("nav-logo");

  const handleActiveNav = (evt) => {
    setActiveNav(`${evt.target.id} active`);
  };
  return (
    <nav className="navbar-container">
      <img className="nav-logo" src={Logo} alt="logo" />
      <ul className="nav-links">
        <NavLink to="/">
          <li>
            <img className={activeNav} src={Home} alt="home logo" />
          </li>
        </NavLink>
        <NavLink to="/movies">
          <li>
            <img
              className={activeNav}
              src={Movies}
              alt="movies logo"
              id="movies"
              onClick={handleActiveNav}
            />
          </li>
        </NavLink>
        <NavLink to="/shows">
          <li>
            <img
              className={activeNav}
              src={TVSeries}
              alt="tv series logo"
              id="shows"
              onClick={handleActiveNav}
            />
          </li>
        </NavLink>
        <NavLink to="/bookmarks">
          <li>
            <img
              className={activeNav}
              src={Bookmark}
              alt="bookmark logo"
              id="bookmarks"
              onClick={handleActiveNav}
            />
          </li>
        </NavLink>
      </ul>
      <img className="user-img" src={User} alt="user" />
    </nav>
  );
};

export default Navbar;
