import React, { useContext, useState } from "react";
import CardDetails from "../Card Details/CardDetails";
import JsonData from "../../data.json";
import Navbar from "../Navbar/Navbar";
import Search from "../Search/Search";
import Trending from "../Trending Content/Trending";

import { SearchContext, StateContext } from "../State Context/StateContext";

const Home = () => {
  const {
    searchValue,
    setSearchValue,
    isInputEmpty,
    setIsInputEmpty,
    handleSearch,
  } = useContext(SearchContext);

  const pageData = JsonData.slice(5);

  const recommendedData = {
    data: searchValue
      ? pageData.filter((film) => film.title.includes(searchValue))
      : pageData,
    header: "Recommended for you",
    placeholder: "Search for movies or TV series",
  };

  return (
    <SearchContext.Provider>
      <Navbar />
      <Search relevantData={recommendedData} />
      <Trending />
      <CardDetails relevantData={recommendedData} />
    </SearchContext.Provider>
  );
};

export default Home;
